ä»£ç  const int N = 30; int a[N]; bitset&lt;1&lt;&lt;10&gt; vis[1&lt;&lt;10]; unordered_map&lt;int, bitset&lt;1&lt;&lt;10&gt; &gt; mp; struct Node{ int dep, sum, sta; }; int main(){ int n, mid; cin&gt;&gt;n, mid = n&gt;&gt;1; for(int i=0;i&lt;n;i++) cin&gt;&gt;a[i]; stable_sort(a, a+n); queue&lt;Node&gt; q; q.push(Node{0, 0, 0}); while(!q.empty()){ int x =q.front().dep, y=q.front().sum, z=q.front().sta; q.pop(); if(x==mid) { mp[y].set(z); continue; } q.push(Node{x+1, y, z}); q.push(Node{x+1, y+a[x], z|(1&lt;&lt;x)}); q.push(Node{x+1, y-a[x], z|(1&lt;&lt;x)}); } q.push(Node{mid, 0, 0}); int ans=0; while(!q.empty()){ int x =q.front().dep, y=q.front().sum, z=q.front().sta; q.pop(); if(x==n){ if(mp.count(y)) { bitset&lt;1&lt;&lt;10&gt; tmp(mp[y]); tmp &amp;=~vis[z], ans+=tmp.count(), vis[z]|=tmp; } continue; } q.push(Node{x+1, y, z}); q.push(Node{x+1, y+a[x], z|(1&lt;&lt;(x-mid))}); q.push(Node{x+1, y-a[x], z|(1&lt;&lt;(x-mid))}); } cout&lt;&lt;ans-1&lt;&lt;endl; }
